function lazyLoadImage(){var e=document.querySelectorAll(".lazy-image");if("IntersectionObserver"in window){var r=new IntersectionObserver(function(e,r){e.forEach(function(e){if(e.isIntersecting){var a=e.target;a.src=a.dataset.src,a.classList.remove("lazy-image"),r.unobserve(a)}})});e.forEach(function(e){r.observe(e)})}else for(var a=0;a<e.length;a++){var n=e[a];n.src=n.dataset.src,n.classList.remove("lazy-image")}}function lazyLoadBackground(){var e=document.querySelectorAll(".lazy-background");if("IntersectionObserver"in window){var r=new IntersectionObserver(function(e,r){e.forEach(function(e){if(e.isIntersecting){var a=e.target;a.classList.remove("lazy-background"),r.unobserve(a)}})});e.forEach(function(e){r.observe(e)})}else for(var a=0;a<e.length;a++){e[a].classList.remove("lazy-background")}}function lazyLoadIframe(){var e=document.querySelectorAll(".lazy-iframe");if("IntersectionObserver"in window){var r=new IntersectionObserver(function(e,r){e.forEach(function(e){if(e.isIntersecting){var a=e.target;$(e.target).find("iframe").attr("src","https://www.youtube.com/embed/"+a.dataset.video),a.classList.remove("lazy-iframe"),r.unobserve(a)}})});e.forEach(function(e){r.observe(e)})}else for(var a=0;a<e.length;a++){e[a].classList.remove("lazy-iframe")}}function lazyloadClass(){var e=document.querySelectorAll(".lazy-class");if("IntersectionObserver"in window){var r=new IntersectionObserver(function(e,r){e.forEach(function(e){if(e.isIntersecting){var a=e.target;a.classList.add(a.dataset.class),a.classList.remove("lazy-class"),r.unobserve(a)}})});e.forEach(function(e){r.observe(e)})}else for(var a=0;a<e.length;a++){e[a].classList.remove("lazy-class")}}$(function(){lazyLoadImage(),lazyLoadBackground(),lazyLoadIframe(),lazyloadClass()});
